mapper.select:
SELECT e.employeeID,e.name,d.departmentName, r.rankName,p.positionName,e.status ,b.branchName FROM employee e JOIN department d ON e.departmentID = d.departmentID JOIN headquarters h ON d.hqID =h.hqID JOIN branch b ON h.branchID = b.branchID JOIN `position` p ON e.positionID = p.positionID JOIN `rank` r ON e.rankID = r.rankID
where b.branchName =#{selectedBranchValue}
ORDER BY e.employeeID ASC ;

		SELECT e.employeeID,e.name,d.departmentName, r.rankName,p.positionName,e.status ,b.branchName
		FROM employee e
		JOIN department d ON e.departmentID = d.departmentID
		JOIN headquarters h ON d.hqID =h.hqID 
		JOIN branch b  ON h.branchID = b.branchID 
		JOIN `position` p ON e.positionID = p.positionID 
		JOIN `rank` r  ON e.rankID = r.rankID

			WHERE e.name LIKE #{searchValue}


        	WHERE e.employeeID = #{searchValue}

		ORDER BY e.employeeID ASC ;

