mapper.select:
UPDATE rank
SET rankName = #{rankName}, isActive = #{isActive}
WHERE rankID = #{rankID}

UPDATE position
SET positionName = #{positionName}, isActive = #{isActive}
WHERE positionID = #{positionID}

